this.getDBM().Files.saveGlobalVariable('commands', cCount);

const then = moment(server.createdAt);
const time = then.from(moment());
const ca = then.format("MMM Do, YYYY");
const lvls = ['none', 'Low', 'Medium', '(╯°□°）╯︵ ┻━┻', '┻━┻彡 ヽ(ಠ益ಠ)ノ彡┻━┻'];

const roles = server.roles.sort((a, b) => b.position - a.position);
let roles1 = roles.filter(r => r.id !== msg.guild.id)
    .array()
    .join(', ');
if (roles1 === undefined || roles1.length == 0) {
    roles1 = "No Roles";
}
if (roles1.length > 1020) {
    roles1 = roles1.substring(0, 1020).replace(/,[^,]+$/, "");
    roles1 = roles1 + "...";
}

const embed = new DiscordJS.RichEmbed()
    .setTitle(`${server.name}'s Information`)
    .setColor('#EE82EE')
    .setThumbnail(server.iconURL)
    .setAuthor(member.displayName, msg.author.displayAvatarURL)
    .addField('**Name**', server.name, true)
    .addField('**ID**', server.id, true)
    .addField('**Owner**', server.owner.user.tag, true)
    .addField('**Verification Level**', lvls[server.verificationLevel], true)
    .addField('**Channels**', server.channels.size, true)
    .addField('**Created At**', `${ca} \n (${time})`, true)
    .addField('**Region**', server.region, true)
    .addField('**AFK Channel**', `${(server.afkChannel && server.afkChannel.name) || "None Set"}`, true)
    .addField('**Members**', server.memberCount, true)
    .addField(`**Roles** (${server.roles.size})`, roles1, true);
msg.channel.send(embed);
